import React, { useContext, useState } from 'react';

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  
  // Access the user data stored in the context
  const [user, setUser] = useContext(MyContext);
  
  console.log(user);
  console.log(email);
  console.log(password);
  
  const handleSubmit = async () => {
    try {
      // Perform a login request using axios
      axios.post(`http://localhost:8080/user/${email}/${password}`)
        .then((res) => {
          console.log(res);
          if (res.data === "Email doesn't exist") {
            // Handle the error
          } else if (res.data === "Wrong password") {
            // Handle the error
          } else {
            // Login successful, update the user data in the context
            setUser(res.data);
          }
        });
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
      <div className="bg-light p-5">
        <h1 className="text-center">Login</h1>
        <div >
          <label htmlFor="email">Email address</label>
          <input type="email" id="email" aria-describedby="emailHelp" placeholder="Enter email" onChange={(e) => { setEmail(e.target.value) }} />
        </div>
        <div className=" mb-4">
          <label htmlFor="password">Password</label>
          <input type="password" id="password" placeholder="Password" onChange={(e) => { setPassword(e.target.value) }} />
        </div>
        <button className="btn btn-primary mb-2" onClick={handleSubmit}>Submit</button>
        <div>
          <a href='/signup'>Or, create an account.</a>
        </div>
      </div>
    </div>
  );
};

export default Login;
