import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons'
import { faShoppingBasket } from '@fortawesome/free-solid-svg-icons';
import { useHistory, Link } from 'react-router-dom'
;


export default function Navbar() {
    const history = useHistory()

    const handleConnexionClick = () => {
        history.push('/login')
    }

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-dark bg-warning">
                <div className="container-fluid">
                    <a className="navbar-brand" href="#" style={{ color: 'red', fontWeight: 'bold' }}>
                        <FontAwesomeIcon icon={faShoppingCart} /> E-Redux
                    </a>
                    <form className="d-flex" role="search">
                        <input className="form-control mr-2" type="search" placeholder="Search" aria-label="Search"></input>
                        <Link to="/basket">
                            <button className="btn btn-secondary mr-2" type="button">
                                <FontAwesomeIcon icon={faShoppingBasket} />
                            </button>
                        </Link>
                        <button className="btn btn-primary" type="button" onClick={handleConnexionClick}>Connexion</button>
                    </form>
                </div>
            </nav>
        </div>
    )
}
