import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import { useState, useEffect } from "react";
import axios from 'axios';
import './ProductList.css';
import Modal from 'react-bootstrap/Modal';


export default function ProductList() {
  const [data, setData] = useState(null);
  const [categories, setCategories] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [showModal, setShowModal] = useState(false);





  const handleClose = () => setShowModal(false);

  
  useEffect(() => {
    async function fetchData() {
      const response = await axios.get("http://localhost:8080/products");
      setData(response.data);

      // Get unique categories
      const categories = Array.from(new Set(response.data.map(product => product.type)));
      setCategories(categories);
    }

    fetchData();
  }, []);

  const handleCategorySelection = (category) => {
    setSelectedCategory(category);
  };





  return (
    <div className="container-fluid bg-image product-list d-flex align-items-center">
      <div className="row w-100 m-0">
        <div className="col-sm-3 category-list p-5">
          {categories?.map(category => (
            <div className={`category ${selectedCategory === category ? 'selected' : ''}`} key={category} onClick={() => handleCategorySelection(category)}>
              {category}
            </div>
          ))}
        </div>
        <div className="col-sm-9 product-grid p-5">
          <div className="row">
            {data?.filter(product => !selectedCategory || product.type === selectedCategory).map(product => (
              <div className="col-sm-6 col-md-4" key={product.id}>
                <div className="card">
                  {/* <img src={product.imageUrl} alt="product" className="card-img-top" /> */}
                  <div className="card-body">
                    <h5 className="card-title">{product.name}</h5>
                    <p className="card-text">{product.type}</p>
                    <p className="card-text">${product.price}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Afficher Produit</Modal.Title>
       

        </Modal.Header>
        <Modal.Body>
          <form>
            <div className="form-group">
              <label htmlFor="name">Nom</label>
              <input type="text" className="form-control" id="name" placeholder={currentProduct.name}  onChange={(e)=>{setName(e.target.value)}} />
            </div>
            <div className="form-group">
              <label htmlFor="type">Type</label>
              <input type="text" className="form-control" id="type"  onChange={(e)=>{setType(e.target.value)}} />

            </div>
            <div className="form-group">
              <label htmlFor="price">Prix</label>
              <input type="number" className="form-control" id="price"  onChange={(e)=>{setPrice(e.target.value)}} />

            </div>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <button className="btn btn-primary" onClick={handleSave}>Enregistrer</button>
        </Modal.Footer>
      </Modal>
        </div>
      </div>
    </div>
  )
}
