import { Formik, Form, Field, ErrorMessage } from 'formik';

const SignIn = () => {
    return (
        <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
            <Formik
                initialValues={{
                    name: '',
                    surname: '',
                    email: '',
                    password: ''
                }}
                validate={values => {
                    let errors = {};
                    if (!values.name) {
                        errors.name = 'Required';
                    }
                    if (!values.surname) {
                        errors.surname = 'Required';
                    }
                    if (!values.email) {
                        errors.email = 'Required';
                    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
                        errors.email = 'Invalid email address';
                    }
                    if (!values.password) {
                        errors.password = 'Required';
                    }
                    return errors;
                }}
                onSubmit={(values, { setSubmitting }) => {
                    setTimeout(() => {
                        console.log(JSON.stringify(values, null, 2));
                        setSubmitting(false);
                    }, 400);
                }}
            >
                {({ isSubmitting }) => (
                    <Form className="bg-light p-5">
                        <h1 className="text-center">Sign In</h1>
                        <div className="form-group">
                            <Field type="text" name="name" placeholder="Enter your name" className="form-control" />
                            <ErrorMessage name="name" component="div" className="text-danger" />
                        </div>
                        <div className="form-group">
                            <Field type="text" name="surname" placeholder="Enter your surname" className="form-control" />
                            <ErrorMessage name="surname" component="div" className="text-danger" />
                        </div>
                        <div className="form-group">
                            <Field type="email" name="email" placeholder="Enter email" className="form-control" />
                            <ErrorMessage name="email" component="div" className="text-danger" />
                        </div>
                        <div className="form-group">
                            <Field type="password" name="password" placeholder="Password" className="form-control" />
                            <ErrorMessage name="password" component="div" className="text-danger" />
                        </div>
                        <button type="submit" disabled={isSubmitting} className="btn btn-primary">
                            Submit
                        </button>
                    </Form>
                )}
            </Formik>
        </div>
    );
};
